import React, { Component } from 'react';
import User from './models/user';
import Movies from './components/Movies';
import MoviesButton from './components/MoviesButton';
import AddMovie from './components/AddMovie';

import {getUser, getUserFavoriteMovies, uploadMovie} from './apiWrapper';

import './App.css';

class App extends Component {
  state = {
    user: '',
    movies: [],
    responseToPost: ''
  };

  componentDidMount() {
    getUser()
      .then(
        (result) => {
          this.setState({
            user: new User(result.username)
          });
        },
        (error) => {
          this.setState({
            error
          });
        }
      )
  }

  handleGetMovies = (user) => {
    getUserFavoriteMovies(user)
      .then(
        (result) => {
          this.setState({
            movies: result
          });
        },
        (error) => {
          this.setState({
            error
          });
        }
      )
  }

  handleUploadMovie = async e => {
    console.log("e===>",e)
    uploadMovie(e).then((result) => {
      handleGetMovies(this.state.user)
        .then(
          (result) => {
            this.setState({
              movies: result
            });
          },
          (error) => {
            this.setState({
              error
            });
          }
        )
      }
  )};

render() {
    const { user, error, movies } = this.state;
    return (
      <div className="App">
        <h1 className="username"> Welcome {user.username}!</h1>

        <MoviesButton getMovies={this.handleGetMovies(user.username)} />

        {movies &&
           <Movies movies={movies}/>
        }

        <AddMovie
          userName={this.state.user.username}
          uploadMovie={this.handleUploadMovie}/>
      </div>
    );
  }
}

export default App;
