import React, { Component } from 'react';
import User from './models/user';
import Movies from './components/Movies';
import MoviesButton from './Button';

import {getUser, getUserFavoriteMovies} from './apiWrapper';

import './App.css';

class App extends Component {
  state = {
    isLoaded: false,
    user: '',
    movies: []
  };

  componentDidMount() {
    getUser()
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            user: new User(result.username)
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  handleClick (user) {
    console.log("user===>", user)
    getUserFavoriteMovies(user)
      .then(response => this.setState({movies: response.data}))
  }

  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('/api/world', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ post: this.state.post }),
    });
    const body = await response.text();

    this.setState({ responseToPost: body });
  };

render() {
    const { user, error, isLoaded, movies } = this.state;
    return (
      <div className="App">
        <h1>{user.username}</h1>
        <MoviesButton getMovies={this.handleClick(user)} />

        <Movies
          movies={this.state.movies}
          handleOnClick={this.handleOnClick}
          clicked={this.clicked}/>
      );


        <form onSubmit={this.handleSubmit}>
          <p>
            <strong>Post to Server:</strong>
          </p>
          <input
            type="text"
            value={this.state.post}
            onChange={e => this.setState({ post: e.target.value })}
          />
          <button type="submit">Submit</button>
        </form>
        <p>{this.state.responseToPost}</p>
      </div>
    );
  }
}

export default App;
