import React, { Component } from 'react';
import User from './models/user';
import Movies from './components/Movies';
import MoviesButton from './components/MoviesButton';
import UploadMovie from './components/UploadMovie';

import {getUser, getUserFavoriteMovies} from './apiWrapper';

import './App.css';

class App extends Component {
  state = {
    isLoaded: false,
    user: '',
    movies: []
  };

  componentDidMount() {
    getUser()
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            user: new User(result.username)
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  handleGetMovies = () => {
    getUserFavoriteMovies(this.state.user)
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            movies: result
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  uploadMovie = (movie) => {
    let movies = [...this.state.movies, movie]
    this.setState({movies: movies})
  }

  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('/api/world', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ post: this.state.post }),
    });
    const body = await response.text();

    this.setState({ responseToPost: body });
  };

render() {
    const { user, error, isLoaded, movies } = this.state;
    return (
      <div className="App">
        <h1 className="username"> Welcome {user.username}!</h1>

        <MoviesButton getMovies={this.handleGetMovies} />
        {movies &&
          <Movies
            movies={movies}
          />
        }
        <UploadMovie uploadMovie={this.uploadMovie}/>
      </div>
    );
  }
}

export default App;
