import React, { Component } from 'react';

class AddMovie extends Component {
  state = {username: null, movie: null, dateWatched: null }

  handleChange = (e) => {
    this.setState({[e.target.id]: e.target.value})
  }

  handleSubmit = (e) => {
    e.preventDefault();
    this.setState({username: this.props.userName})
    this.props.uploadMovie({...this.state});
  }

  render() {
    return (
      <div className="upload-movie">
        <form onSubmit={this.handleSubmit}>
          <label>
            Movie's Title:
            <input type="text" id='movie' onChange={this.handleChange} />
          </label>
          <label>
            Date:
            <input type="text" id='dateWatched' onChange={this.handleChange} />
          </label>
          <button>Submit</button>
        </form>
      </div>
    )
  }
}

export default AddMovie;
