export async function getUser() {
  const response = await fetch('/api/user');
  const body = await response.json();
  if (response.status !== 200) throw Error(body.message);

  return body;
};

export async function getUserFavoriteMovies(user) {
  const response = await fetch(`/api/movies/${user.username}`);
  const body = await response.json();
  if (response.status !== 200) throw Error(body.message);
  return body;
};

export async function uploadMovie(data) {
  const requestData = await data.map((item) => {
    // let date = item.dateWatched.split('-')
    return item.dateWatched = Date.UTC("2020", '1', '2')
    // return item.dateWatched = Date.UTC(...date)
  })
  //2020-02-19
  console.log('data===>', data);
  const response = await fetch('/api/movie', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ movie: data }),
  });

  const body = await response.json();
  return body
};
